import React, { useRef, useState } from "react";
import { techstack } from '../techstack';
import '../styles.css'; 

function Home() {
  const containerRef = useRef(null);

  // State for filtering
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [filteredTechstack, setFilteredTechstack] = useState(techstack);

  // Filters array
  const filters = ["Advance", "Good", "Familiar"];

  // Function to handle filter button click
  const handleFilterButtonClick = (category) => {
    let newSelectedFilters = [...selectedFilters];
    if (newSelectedFilters.includes(category)) {
      newSelectedFilters = newSelectedFilters.filter((filter) => filter !== category);
    } else {
      newSelectedFilters.push(category);
    }
    setSelectedFilters(newSelectedFilters);

    if (newSelectedFilters.length === 0) {
      setFilteredTechstack(techstack);
    } else {
      setFilteredTechstack(
        techstack.filter((tech) => newSelectedFilters.includes(tech.category))
      );
    }
  };

  // Function to handle smooth scrolling
  const scrollToNextPage = () => {
    const container = containerRef.current;
    if (container) {
      container.scrollTo({
        top: container.scrollTop + container.clientHeight,
        behavior: "smooth",
      });
    }
  };

  return (
    <div className="page-container" ref={containerRef}>
      <div className="page page1">
        <div className="flex">
          <div className="mt-60 font-semibold">
            <h1 className="text-left ml-60 text-3xl font-extrabold">
              Hi There !
            </h1>
            <h1 className="text-left ml-60 text-3xl font-extrabold">
              I'm a full stack{" "}
              <span className="developer-text text-pink-700">Developer</span>
            </h1>
            <div className="mt-10">
              <button className="px-4 py-2 ml-60 border-2 border-solid border-green-500 bg-green-500 hover:bg-green-600 hover:text-white">
                Hire me
              </button>
              <button className="px-4 py-2 ml-4 border-2 border-solid border-green-500 hover:bg-green-600 hover:text-white">
                Download CV
              </button>
            </div>
          </div>
          <div className="AnimeImage ml-40 mt-16">
            <img
              src="/images/anime.png"
              alt="Anime Image"
              className="w-full h-auto max-w-md"
            />
          </div>
        </div>
      </div>
      <div className="page page2">
        <div>
          <div>
            <h1 className="mt-10 text-4xl font-bold text-center">About me</h1>
          </div>
          <div className="flex w-full mt-10">
            <div className="photo w-1/2 flex justify-center items-center">
              <img
                src="/images/photo.jpeg"
                alt="Image"
                className="w-72 h-auto max-w-md"
              />
            </div>
            <div className="details w-1/2  items-center">
              <div className="text-3xl font-semibold text-pink-700">
                Pamodhya Thotahewa
              </div>
              <div className="text-xl font-thin text-green-500">
                Full stack web developer
              </div>
              <div className="flex mt-10">
                <button className="border-2 border-solid border-yellow-300 px-8 py-2 ">
                  <p className="font-semibold text-blue-900">Location</p>
                  <p>Matale,Sri Lanka</p>
                </button>
                <button className="border-2 border-solid border-yellow-300 px-8 py-2 ml-10">
                  <p className="font-semibold  text-blue-900">Age</p>
                  <p>22</p>
                </button>
                <button className="border-2 border-solid border-yellow-300 px-8 py-2 ml-10 ">
                  <p className="font-semibold  text-blue-900">Projects</p>
                  <p>4</p>
                </button>
              </div>
              <div className="details w-2/3 flex justify-center items-center mt-12">
                <div className="content-center">
                  <p className="text-justify">
                    Passionate and driven ReactJS developer with a strong foundation
                    in MERN Stack and NextJS. Dedicated to creating dynamic and
                    user-centric web applications. Eager to contribute my expertise in
                    frontend frameworks, modern UI/UX design, and responsive
                    development to a forward-thinking team, while continuously
                    learning and growing in the ever-evolving world of web
                    development.
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="page page3">
        <div className="container">
          <div className="buttons-container">
            {filters.map((category, idx) => (
              <button
                key={idx}
                onClick={() => handleFilterButtonClick(category)}
                className={`button ${selectedFilters.includes(category) ? "active" : ""}`}
              >
                {category}
              </button>
            ))}
          </div>
          <div className="techstack-container">
            {filteredTechstack.map((tech, index) => (
              <div key={index} className="techstack">
                <img src={tech.image} alt={tech.name} className="techstack-image" />
                <h3>{tech.name}</h3>
                <p className="category">{tech.category}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
      <div className="page page4">
        {/* Content for page 4 */}
      </div>
      <div className="page page5">
        {/* Content for page 5 */}
      </div>
      <button onClick={scrollToNextPage}>Next Page</button>
    </div>
  );
}

export default Home;
